    package com.ymu.demo.springboot2apiversion.version;

    import org.springframework.web.servlet.mvc.condition.RequestCondition;

    import javax.servlet.http.HttpServletRequest;
    import java.util.regex.Pattern;

    public class ApiVersionCondition implements RequestCondition<ApiVersionCondition> {

        private int apiVersion;

        public ApiVersionCondition(int apiVersion){
            this.apiVersion = apiVersion;
        }

        public ApiVersionCondition combine(ApiVersionCondition other) {
            // 
            return new ApiVersionCondition(other.getApiVersion());
        }

        public ApiVersionCondition getMatchingCondition(HttpServletRequest request) {
        	String path = request.getServletPath();
        	if (path == null) {
    			return null;
    		}
    		String contentVersion = request.getHeader("Content-Version"); //httpapiurl
            if (null == contentVersion || "".equals(contentVersion)) {
                throw new IllegalArgumentException("Content-Versionnull");
            }
            if (!isInteger(contentVersion)) {
                throw new IllegalArgumentException("Content-Version");
            }

            int version = Integer.valueOf(contentVersion).intValue();
            if(version >= this.apiVersion) { //  
                return this;
            }
            return null;
        }

        public int compareTo(ApiVersionCondition other, HttpServletRequest request) {
            // 
            return other.getApiVersion() - this.apiVersion;
        }

        public int getApiVersion() {
            return apiVersion;
        }

        /**
         * 
         * @param str
         * @return
         */
        private boolean isInteger(String str) {
            Pattern pattern = Pattern.compile("^[-\\+]?[\\d]*$");
            return pattern.matcher(str).matches();
        }

    }

