        Set<String> permissions = getPermissionByUsername(username);

        SimpleAuthorizationInfo authorizationInfo = new SimpleAuthorizationInfo();
        authorizationInfo.setStringPermissions(permissions); //
        authorizationInfo.setRoles(roles); //

        return authorizationInfo;
    }

    /**
     * 
     * @param username
     * @return
     */
    private Set<String> getPermissionByUsername(String username) {
        Set<String> permissions = new HashSet<>();
        permissions.add("admin:delete");
        permissions.add("user:select");
        permissions.add("user:update");
        return permissions;
    }

    /**
     * 
     * @param username
     * @return
     */
    private Set<String> getRolesByUsername(String username) {
        Set<String> roles = new HashSet<>();
        roles.add("admin");
        roles.add("user");
        return roles;
    }

    @Override
    protected AuthenticationInfo doGetAuthenticationInfo(AuthenticationToken token) throws AuthenticationException {

        //,
        String username = (String) token.getPrincipal();
        System.out.println("username:" + username);

        //
        String pwd = getPwdByUsername(username);
        if (pwd == null) {
            return null;
        }

        SimpleAuthenticationInfo authenticationInfo = new SimpleAuthenticationInfo(username,pwd,realmName);
        authenticationInfo.setCredentialsSalt(ByteSource.Util.bytes("aaa")); //

        return authenticationInfo;
    }

    /**
     * 
     * @param username 
     * @return
     */
    private String getPwdByUsername(String username) {
        return userMap.get(username);
    }


}

